cy.request({
  method: 'GET',
  url: Cypress.env('mock_url') + '/users/1',
  headers: {
    'Content-type': 'application/json'
  }
}).as('request');
cy.get('@request').its('status').should('equal', 200);
cy.get('@request').its('body').then(function (response) {
  cy.log(JSON.stringify(response));
  expect(response).deep.equal({
    "id": 1,
    "name": "Jonas",
    "surname": "T."
  });
});

cy.request({
  method: 'GET',
  url: Cypress.env('mock_url') + '/users?q=J',
  headers: {
    'Content-type': 'application/json'
  }
}).as('request');
cy.get('@request').its('body').then(function (response) {
  response.forEach(function (element) {
    expect(element.name).to.be.contains('J');
  });
});

cy.request({
  method: 'GET',
  url: Cypress.env('mock_url') + '/users?_page=1&_limit=1',
  headers: {
    'Content-type': 'application/json'
  }
}).as('request');
cy.get('@request').its('body').then(function (response) {
  expect(response.length).to.be.equal(1);
});
